import { Form} from 'react-bootstrap';
import MyCard from '../UI/MyCard';
import classes from './AddUser.module.scss';
import MyButton from '../UI/Button';
import {useState} from 'react';
import ErrorModal from '../UI/ErrorModal';


const AddUser = (props) => {
    const initialState = {
        username: '',
        age: '',
    }
    const [formValues, setFormValues] = useState(initialState)
    const [isError, setIsError] = useState(false)

    const handleInputChange = (event) => {
        setFormValues({
            ...formValues,
            [event.target.name]: event.target.value,
        })
    }

    const addUserHandler = (event) => {
        event.preventDefault();
        if (formValues['username'].trim().length === 0 || formValues['age'].trim().length === 0) {
            setIsError(true)
        }
        if ( formValues['age'] < 1) return
        props.setUsers((initialUsers) => [...initialUsers, formValues])
        setFormValues(initialState)
    }

    return (
        <>
            <ErrorModal title="This here's an error" message="something done fucked up!" isError={isError}/>
            <MyCard title="The title" header="The header" className={classes.input}>

                <Form onSubmit={addUserHandler}>
                    <Form.Group className="mb-3">
                        <Form.Label htmlFor="username">Username</Form.Label>
                        <Form.Control
                            id="username"
                            name="username"
                            type="text"
                            onChange={handleInputChange}
                            value={formValues['username']}
                        />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Label htmlFor="age">Age (Years)</Form.Label>
                        <Form.Control
                            id="age"
                            name="age"
                            type="number"
                            onChange={handleInputChange}
                            value={formValues['age']}
                        />
                    </Form.Group>
                    <MyButton
                        type="button"
                        onClick={addUserHandler}
                    >Add User</MyButton>
                </Form>
            </MyCard>
        </>
    )
}

export default AddUser;
